//This program has variables of several of the integer types.
public class integerVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int checking; //int variable
		byte miles; //byte variable
		short minute; // short variable
		long diameter; //long variable
		
		checking = -20;
		miles = 104;
		minute =120;
		diameter = 10000;
		
		System.out.println("We have made a journey of "+miles + "miles.");
		System.out.println("It took us "+minute + "minutes.");
		System.out.println("Our account balance is $"+checking);
		System.out.println("The galaxy is "+ diameter+"light years in diameter.");

	}

}
